mapper.select:
select u.*, r.roleName
from smbms_user u, smbms_role r
where u.userRole = r.id

    and u.userName like CONCAT('%', #{userName}, '%')


    and u.userRole = #{roleId}

order by creationDate DESC limit #{from}, #{pageSize}

select *
from smbms_user
where userName like CONCAT('%', #{uName}, '%') and userRole = #{uRole}

mapper.update:
update smbms_user
userCode = #{userCode}, userName = #{userName}, userPassword = #{userPassword}, gender = #{gender}, birthday = #{birthday}, phone = #{phone}, address = #{address}, userRole = #{userRole}, modifyBy = #{modifyBy}, modifyDate = #{modifyDate}
where id = #{id}

update smbms_user
set userPassword = #{userPassword}
where id = #{id}

mapper.select:
select COUNT(1) from smbms_user u,smbms_role r where u.userRole = r.id
and u.userName like concat('%', #{userName}, '%')
and u.userRole = #{userRole}
